import json
from typing import Any, Dict, Optional

import aiohttp

from app.config import settings
from app.utils.logger import logger

HEYGEN_API_ENDPOINTS = {
    "start_session": "/v1/streaming.start",
    "stop_session": "/v1/streaming.stop",
    "interrupt_speech": "/v1/streaming.interrupt",
    "speak": "/v1/streaming.task",
}


class HeygenAPIClient:
    def __init__(self, api_key: Optional[str] = None, base_url: Optional[str] = None):
        self.api_key = api_key or settings.HEYGEN_API_KEY
        self.base_url = base_url or settings.HEYGEN_BASE_URL
        self.headers = {
            "Content-Type": "application/json",
            "X-Api-Key": self.api_key,
        }

    async def _make_request(
        self, endpoint: str, payload: Dict[str, Any], method: str = "POST"
    ) -> Optional[Dict[str, Any]]:
        url = f"{self.base_url}{endpoint}"

        async with aiohttp.ClientSession() as session:
            try:
                async with session.request(
                    method=method, url=url, headers=self.headers, json=payload
                ) as response:
                    if response.status == 200:
                        try:
                            response_text = await response.text()
                            return json.loads(response_text)

                        except json.JSONDecodeError:
                            logger.error(
                                f"Heygen API request failed: {endpoint} - "
                                f"Status: {response.status} - "
                                f"Response: {response_text}"
                            )
                            return None

                        except Exception as e:
                            logger.error(
                                f"Error parsing Heygen API response: {e} - "
                                f"Response: {response_text}"
                            )
                            return None

                    else:
                        logger.error(
                            f"Heygen API request failed: {endpoint} - "
                            f"Status: {response.status}"
                        )
                        return None

            except Exception as e:
                logger.error(f"Error making request to {endpoint}: {str(e)}")
                return None

    async def start_session(self, session_id: str, version: str = "v2") -> bool:
        payload = {
            "session_id": session_id,
            "version": version,
        }

        result = await self._make_request(
            HEYGEN_API_ENDPOINTS["start_session"], payload
        )

        return result is not None

    async def speak(
        self, session_id: str, text: str, task_type: str = "repeat"
    ) -> bool:
        payload = {
            "session_id": session_id,
            "text": text,
            "task_type": task_type,
        }

        result = await self._make_request(HEYGEN_API_ENDPOINTS["speak"], payload)
        return result is not None

    async def interrupt_speech(self, session_id: str) -> bool:
        payload = {"session_id": session_id}
        result = await self._make_request(
            HEYGEN_API_ENDPOINTS["interrupt_speech"], payload
        )

        return result is not None

    async def stop_session(self, session_id: str) -> bool:
        payload = {"session_id": session_id}
        result = await self._make_request(HEYGEN_API_ENDPOINTS["stop_session"], payload)

        return result is not None
