import asyncio
from typing import AsyncIterable, Optional

from livekit import rtc
from pydantic import BaseModel

from app.clients.heygen import HeygenAPIClient
from app.utils.logger import logger


class HeygenMetadata(BaseModel):
    session_id: str
    access_token: str
    livekit_url: str


class HeygenStreamClient:
    def __init__(
        self,
        heygen_session: HeygenMetadata,
        api_client: Optional[HeygenAPIClient] = None,
    ):
        self.heygen_session = heygen_session
        self.api_client = api_client or HeygenAPIClient()
        self.room: Optional[rtc.Room] = None
        self.audio_stream: Optional[rtc.AudioStream] = None

    async def initialize(
        self, video_source: rtc.VideoSource, audio_source: rtc.AudioSource
    ) -> bool:
        try:
            session_started = await self.api_client.start_session(
                self.heygen_session.session_id
            )
            if not session_started:
                return False

            await self._setup_stream_handlers(video_source, audio_source)

            return True

        except Exception as e:
            logger.error(f"Failed to initialize Heygen stream: {e}")
            return False

    async def _setup_stream_handlers(
        self, video_source: rtc.VideoSource, audio_source: rtc.AudioSource
    ):
        if not self.heygen_session:
            raise RuntimeError("Session not initialized")

        self.room = rtc.Room()

        async def receive_video(video_stream: rtc.VideoStream):
            async for frame_event in video_stream:
                try:
                    video_source.capture_frame(frame=frame_event.frame)
                except Exception as e:
                    logger.error(f"Video frame processing error: {e}")

        async def receive_audio(audio_stream: rtc.AudioStream):
            async for frame_event in audio_stream:
                try:
                    await audio_source.capture_frame(frame=frame_event.frame)
                except Exception as e:
                    logger.error(f"Audio frame processing error: {e}")

        @self.room.on("track_subscribed")
        def on_track_subscribed(
            track: rtc.Track,
            publication: rtc.RemoteTrackPublication,
            participant: rtc.RemoteParticipant,
        ):
            logger.info(f"Track subscribed: {publication.sid}")

            if track.kind == rtc.TrackKind.KIND_VIDEO:
                video_stream = rtc.VideoStream(track)
                asyncio.ensure_future(receive_video(video_stream))

            elif track.kind == rtc.TrackKind.KIND_AUDIO:
                self.audio_stream = rtc.AudioStream(track)
                asyncio.ensure_future(receive_audio(self.audio_stream))

        await self.room.connect(
            self.heygen_session.livekit_url, self.heygen_session.access_token
        )

    async def speak(self, text: str | AsyncIterable[str]) -> bool:
        if not self.heygen_session:
            logger.error("No active session")
            return False

        if isinstance(text, str):
            return await self.api_client.speak(self.heygen_session.session_id, text)

        text_to_speak = ""
        async for chunk in text:
            text_to_speak += chunk

        return await self.api_client.speak(
            self.heygen_session.session_id, text_to_speak
        )

    async def interrupt(self) -> bool:
        if not self.heygen_session:
            return False

        return await self.api_client.interrupt_speech(self.heygen_session.session_id)

    async def close(self):
        if self.heygen_session:
            await self.api_client.stop_session(self.heygen_session.session_id)
            self.heygen_session = None

        if self.room:
            await self.room.disconnect()
            self.room = None
```

